import React, { useState, useEffect } from "react";

const CanOpener = () => {
    const [participantsState, setParticipantsState] = useState([
        {
            name: "",
        },
    ]);
    const blankUser = { name: "" };
    const [userList, setUserList] = useState([{ ...blankUser }]);
    const addParticipant = () => {
        setParticipantsState([...participantsState, { ...blankUser }]);
    };

    const handleChange = (e) =>
        setParticipantsState({
            ...participantsState,
            [e.target.name]: [e.target.value],
        });
    const handleSubmit = (e) => {
        e.preventDefault();
        setUserList(participantsState);
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Name</label>
                <input
                    type="text"
                    name="name"
                    id="nickName"
                    value={participantsState.name}
                    onChange={handleChange}
                ></input>
                <input
                    type="submit"
                    value="Add user"
                    onClick={addParticipant}
                ></input>
            </form>
            {userList.map()}
        </div>
    );
};
export default CanOpener;
