import React, { useState, useEffect } from "react";

const CanOpener = () => {
    const blankParticipant = { name: "" };
    const [participantsState, setParticipantsState] = useState([
        { ...blankParticipant },
    ]);
    const handleChange = (e) =>
        setParticipantsState({
            ...participantsState,
            [e.target.name]: [e.target.value],
        });
    const addParticipant = () => {
        setParticipantsState([...participantsState, { ...blankParticipant }]);
    };
    return (
        <div>
            <form>
                <label htmlFor="nickName">Nickname</label>
                <input
                    type="text"
                    name="nickName"
                    id="nickName"
                    value={participantsState.name}
                    onChange={handleChange}
                ></input>
                <input type="button" value="Add user"></input>
                {participantsState.map((value, index) => {
                    const participantId = `name-${index}`;
                    return (
                        <div key={`user-${index}`}>
                            <label htmlFor={participantId}>{`User #${
                                index + 1
                            }`}</label>
                            <input
                                type="text"
                                name={participantId}
                                data-index={index}
                                id={participantId}
                                className="nickName"
                            ></input>
                        </div>
                    );
                })}

                <input type="submit" value="Submit" onClick={addParticipant} />
            </form>
        </div>
    );
};
export default CanOpener;
