import React, { useState, useEffect } from "react";

const CanOpener = () => {
    const [participantsState, setParticipantsState] = useState([
        {
            name: "",
        },
    ]);
    const handleChange = (e) =>
        setParticipantsState({
            ...participantsState,
            [e.target.name]: [e.target.value],
        });

    const addParticipant = () => {
        setParticipantsState(...participantsState);
    };
    return (
        <div>
            <form>
                <label htmlFor="name">Name</label>
                <input
                    type="text"
                    name="name"
                    id="nickName"
                    value={participantsState.name}
                    onChange={handleChange}
                ></input>
                <input
                    type="button"
                    value="Add user"
                    onClick={addParticipant}
                ></input>
            </form>
            <ul>
                {participantsState.map((index, value) => {})}
                <li></li>
            </ul>
        </div>
    );
};
export default CanOpener;
