import React, { useState, useEffect } from "react";

const AddUser = () => {
    const useSignUpForm = (callback) => {
        const [inputs, setInputs] = useState({});

        const handleSubmit = (e) => {
            if (e) {
                e.preventDefault();
            }
        };
        const handleInputChange = (e) => {
            e.persist();
            setInputs((inputs) => ({
                ...inputs,
                [e.target.name]: e.target.value,
            }));
        };
        return {
            handleSubmit,
            handleInputChange,
            inputs,
        };
    };
    useEffect(() => {
