    }, []);
    return (
        <form>
            <div>
                <label>Name</label>
                <input
                    type="text"
                    name="nickName"
                    value={inputs.nickName}
                    onChange={handleInputChange}
                ></input>
                <button type="submit" onSubmit={handleSubmit}>
                    Add user
                </button>
            </div>
        </form>
    );
};
export default AddUser;
