import React, { useState, useEffect } from "react";

const CanOpener = () => {
    const blankParticipant = { name: "" };
    const [participantsState, setParticipantsState] = useState([
        { ...blankParticipant },
    ]);
    const handleChange = (e) => {
        const updatedParticipants = [...participantsState];
        updatedParticipants[e.target.dataset.idx][e.target.className] =
            e.target.value;
        setParticipantsState(updatedParticipants);
    };

    const addParticipant = () => {
        setParticipantsState([...participantsState, { ...blankParticipant }]);
    };
    return (
        <div>
            <form>
                <label htmlFor="nickName">Nickname</label>
                <input
                    type="text"
                    name="nickName"
                    id="nickName"
                    value={participantsState.name}
                    onChange={handleChange}
                ></input>
                <input type="button" value="Add user"></input>
            </form>
            <ul>
                {participantsState.map((value, index) => {
                    return <li key={`user-${index}`}>{value.name}</li>;
                })}
            </ul>
        </div>
    );
};
export default CanOpener;
